const BillContext = createContext();

const BillProvider = ({children}) => {

    const [bills,setBills] = useState([]);

    const updateBills = (bill) =>{

    useEffect(() => {
        setBills(JSON.parse)
    }, [setBills]) //-->setBills == componentDidMount --/
    };

    return (
        <BillContext.Provider value ={{bills,
        updateBills}}>
        {children}

        </BillContext.Provider>
    );
}

